import React, {useState, useEffect} from 'react'
import axios from 'axios'
import './App.css';

function App() {
  const [pokemon, setPokemon] = useState()

  const fetchRandomPokemon = () => {
    const pokemonId = randomNum();
    const URL = `https://pokeapi.co/api/v2/pokemon/${pokemonId}/`;
    axios.get(URL)
      .then(res => {
        setPokemon(res.data)
        console.log(res.data)
      })
      .catch(err => {
        console.log(err.message)
      })
  }

  useEffect(() => {
    fetchRandomPokemon();
  }, [])

  return (
    <div>
      <h1>Random Pokemon Generator</h1>
      <button className="myButton" onClick={fetchRandomPokemon}>
        Click here to generate Pokemon
      </button>
      {/* Display Pokemon details here */}
      {pokemon && <p>{pokemon.name}</p>}
    </div>
  )
}


export default App;
