import React, {useState, useEffect} from 'react'
import axios from 'axios'
import './App.css';

function Card({}) {
  <div>
    <h2>{name}</h2>
  </div>
}

function randomNum() {
  const number = Math.floor(Math.random() * 1000)
  return number;
}

function App() {
  const [pokemon, setPokemon] = useState()

  const fetchRandomPokemon = () => {
    const pokemonId = randomNum();
    const URL = `https://pokeapi.co/api/v2/pokemon/${pokemonId}/`
    axios.get(URL)
    .then(res => {
      setPokemon(res.data)
    })
    .catch(err => {
      console.log(err.message)
    })
  }

  useEffect(() => {
      fetchRandomPokemon();
  }, [])


  return (
    <div>
      <h1>Random Pokemon Generator</h1>
      <Card pokemonName={pokemon.name}/>
      <button className="myButton" onClick={fetchRandomPokemon}>{pokemon.name}</button>
    </div>
  )
}

export default App;
