import React, {useState, useEffect} from 'react'
import axios from 'axios'
import './App.css';


function randomNum() {
  const number = Math.floor(Math.random() * 10)
  return number;
}

function App() {
  const [pokemon, setPokemon] = useState()

  const fetchRandomPokemon = () => {
    const pokemonId = randomNum();
    const URL = `https://pokeapi.co/api/v2/pokemon/${pokemonId}/`
    axios.get(URL)
    .then(res => {
      setPokemon(res.data)
    })
    .catch(err => {

    })
  }

  useEffect(() => {
      function getPokemon(){
        axios.get(URL)
        .then(res => {
          setPokemon(res.data)
          console.log(res.data)
        })
        .catch(err => {
          console.log(err.message)
        })
      }
      getPokemon()
  }, [])


  return (
    <div>
      <h1>Random Pokemon Generator</h1>
      <p>{pokemon.name}</p>
      <button className="myButton">Click here to generate Pokemon</button>
    </div>
  )
}

export default App;
